mapper.select:
select
  id, parent_id, name, ename, url, description, create_time, update_time
from oauth2.tb_permission


        and id = #{id}


        and parent_id = #{parentId}


        and name = #{name}


        and ename = #{ename}


        and url = #{url}


        and description = #{description}


        and create_time = #{createTime}


        and update_time = #{updateTime}
